import React from 'react';

const Product = ({ item }) => {
  return (
    <div className="container w-full  max-w-sm mx-auto bg-white rounded-lg shadow-md overflow-hidden mb-10">
      <img src={item.image} alt={item.title} className="w-full h-56 object-cover object-center" />
      <div className="p-4">
        <h3 className="text-gray-900 font-bold text-xl mb-2">{item.title}</h3>
        <p className="text-gray-700 text-base mb-4">{item.description}</p>
        <p className="text-indigo-500 font-semibold text-lg">{item.category}</p>
        <button className="mt-4 bg-indigo-500 hover:bg-indigo-600 text-white py-2 px-4 rounded">
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default Product;
