import React from 'react';
import { useSelector } from 'react-redux';

const Cart = () => {
  const { cart } = useSelector((state) => state);

  return (
    <div className="p-4">
      {cart.length === 0 ? (
        <div className='text-center text-gray-600 py-8'>There is no product added to the cart.</div>
      ) : (
        <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
          {cart.map((post) => (
            <div key={post.id} className="p-4 border border-gray-300 rounded-lg shadow-md">
              <img src={post.image} alt={post.title} className="w-full h-48 object-cover" />
              <h3 className="text-gray-900 font-semibold text-xl my-2">{post.title}</h3>
              <p className="text-gray-600">{post.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
