import React, { useEffect, useState } from 'react';
import { apiUrl } from '../Data';
import Product from '../Components/Product';

const Products = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      const Items = await fetch(apiUrl);
      const mainItems = await Items.json();
      setData(mainItems);
    } catch (error) {
      console.log('There is an error', error);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="container mx-auto p-4">
      {loading ? (
        <div className='loading flex justify-center items-center'>Loading...</div>
      ) : (
        <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4'>
          {data.map((item) => (
            <div key={item.id} className='bg-white rounded-lg p-4 shadow-md hover:shadow-lg transition'>
              <img
                src={item.image} // Make sure you have an image URL in your product data
                alt={item.name}
                className='w-full h-40 object-cover mb-4 rounded-lg'
              />
              <div className='font-semibold text-lg mb-2'>{item.name}</div>
              <div className='text-gray-600 mb-4'>${item.price}</div>
              <div className='font-extralight'>{item.description}</div>
              {/* Additional product details can be added here */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Products;
